static VOID control_thread_entry(ULONG input)
{
INT             failed_tests = 0;
TEST_DATA      *test = test_data_list;
GX_PIXELMAP     outmap;
UINT            status;
GX_IMAGE_READER image_reader;
ULONG           memory_pool_availlable;

    while(test->test_data)
    {
        /* Decode invalid png format. */
        status = _gx_image_reader_png_decode(test->test_data, test->data_size, &outmap);
        EXPECT_EQ(test->error_code, status);
        test++;
    }
    memory_pool_availlable = rotate_pool.tx_byte_pool_available;

    gx_image_reader_create(&image_reader,
                            test_data_memory_leak,
                            sizeof(test_data_memory_leak),
                            GX_COLOR_FORMAT_565RGB, GX_IMAGE_READER_MODE_ALPHA);

    status = gx_image_reader_start(&image_reader, &outmap);
    EXPECT_EQ(GX_SUCCESS, status);
    if(outmap.gx_pixelmap_data)
    {
        rotate_memory_free(outmap.gx_pixelmap_data);
    }

    if(outmap.gx_pixelmap_aux_data)
    {
        rotate_memory_free(outmap.gx_pixelmap_aux_data);
    }
    EXPECT_EQ(memory_pool_availlable, rotate_pool.tx_byte_pool_available);

    tx_byte_pool_create(&test_memory_pool, "scratchpad", test_memory, TEST_MEMORY_SIZE);
 
    /* Decode without enough memory. */
    gx_system_memory_allocator_set(test_memory_allocate, test_memory_free);
    status = _gx_image_reader_png_decode(test_data_35, sizeof(test_data_35), &outmap);
    EXPECT_EQ(GX_SYSTEM_MEMORY_ERROR, status);

    /* Decode withou memory allocator. */
    gx_system_memory_allocator_set(GX_NULL, GX_NULL);
    status = _gx_image_reader_png_decode(test_data_35, sizeof(test_data_35), &outmap);
    EXPECT_EQ(GX_SYSTEM_MEMORY_ERROR, status);

    /* Test memory size id not enough to allocat gx_png_trans. */
    tx_byte_pool_delete(&test_memory_pool);
    tx_byte_pool_create(&test_memory_pool, "test_memory_pool", test_memory_1, TEST_MEMORY_SIZE_1);
    gx_system_memory_allocator_set(test_memory_allocate, test_memory_free);
    status = _gx_image_reader_png_decode(TBBN1G04_pixelmap_data, sizeof(TBBN1G04_pixelmap_data), &outmap);
    EXPECT_EQ(GX_SYSTEM_MEMORY_ERROR, status);
    
    tx_byte_pool_delete(&test_memory_pool);
    tx_byte_pool_create(&test_memory_pool, "test_memory_pool", test_memory_2, TEST_MEMORY_SIZE_2);
    status = _gx_image_reader_png_decode(TBBN1G04_pixelmap_data, sizeof(TBBN1G04_pixelmap_data), &outmap);
    EXPECT_EQ(GX_SYSTEM_MEMORY_ERROR, status);

    tx_byte_pool_delete(&test_memory_pool);
    tx_byte_pool_create(&test_memory_pool, "test_memory_pool", test_memory_3, TEST_MEMORY_SIZE_3);
    status = _gx_image_reader_png_decode(BASN3P08_pixelmap_data, sizeof(BASN3P08_pixelmap_data), &outmap);
    EXPECT_EQ(GX_SYSTEM_MEMORY_ERROR, status);

    outmap.gx_pixelmap_format = 0;
    status = _gx_image_reader_pixel_read_callback_set(&image_reader, &outmap);
    EXPECT_EQ(GX_NOT_SUPPORTED, status);

    /* Output test result. */
    if (failed_tests == 0)
    {
        gx_validation_print_test_result(TEST_SUCCESS);
        exit(0);
    }
    else 
    {
        gx_validation_print_test_result(TEST_FAIL);
        exit(1);
    }
}
